import * as b from "bobril";
import { Button } from "../../src/lib";
import { getTestStore } from "./store";

export function createLayout(): void {
    b.init(() => {
        return [
            Button({
                label: "Button 1",
                onClick: () => {
                    getTestStore().clickCount += 1;
                    b.invalidate();
                },
            }),
            Button({
                label: "Button disabled",
                isDisabled: true,
                onClick: () => {
                    getTestStore().clickCount += 1;
                    b.invalidate();
                },
            }),
        ];
    });
}
