import { BBSeeker, BBBot } from "bbseeker-extended";
import * as Button from "../../src/lib";
import * as b from "bobril";
import { Selectors } from "./selector";

describe("bobwai--button test", () => {
    beforeEach(() => {
        b.init(() => {
            return [
                Button.create({
                    label: "Button",
                    onClick: () => {},
                }),
            ];
        });
    });
    it("should find the right element according the key", async () => {
        await BBBot.checkIfFullyRendered(5);
        const result = await BBSeeker.findElements(Selectors.id);
        expect(result).toBeDefined();
        expect(result.length).toBe(1);
        expect(result[0]).toBeDefined;
        const label = result[0].textContent;
        expect(label).toBe("Button");
    });
});
