import * as b from "bobril";
import { Gradient } from "../../src/lib";

export default function getGradientsExample(): b.IBobrilNode[] {
    return Object.keys(Gradient).map(i => {
        return getGradientItem(i, (<any>Gradient)[i]);
    });
}

function getGradientItem(name: string, value: string): b.IBobrilNode {
    return b.styledDiv(
        [b.styledDiv("", [mockGradientItemExampleStyle, { background: value }]), b.styledDiv(name)],
        mockGradientItemWrapperExampleStyle
    );
}

const mockGradientItemExampleStyle = b.styleDef({
    height: 200,
    width: 200,
    margin: 15,
    display: "inline-block"
});
const mockGradientItemWrapperExampleStyle = b.styleDef({
    display: "inline-block",
    textAlign: "center",
    marginBottom: 15
});
