import * as b from "bobril";
import * as constants from "./publicConstants";
import { getTransparentColor } from "./helper";

// Each application and sample company can have different

// ---------- Application color -----------
export const DefaultApplicationColor = "#72bf44"; // 'rgba(114, 191, 78, 1)'
let currentApplicationColor = DefaultApplicationColor;

export function Application(): string {
    return currentApplicationColor;
}

export function Application_10(): string {
    return getTransparentColor(currentApplicationColor, 0.1);
}

export function Application_30(): string {
    return getTransparentColor(currentApplicationColor, 0.3);
}

export function Application_40(): string {
    return getTransparentColor(currentApplicationColor, 0.4);
}

export function Application_64(): string {
    return getTransparentColor(currentApplicationColor, 0.64);
}

export function setApplicationColor(color: string): void {
    currentApplicationColor = color;
    b.invalidateStyles();
}

export function restoreDefaultApplicationColor(): void {
    setApplicationColor(DefaultApplicationColor);
}

