// ---------- Chart colors - list sorting made by UX: 500/300/100/700 -----------
const chartColorsSortedList = [
    constants.Chart_Green500,
    constants.Chart_Blue500,
    constants.Chart_Violet500,
    constants.Chart_Yellow500,
    constants.Chart_Brown500,
    constants.Chart_Purple500,
    constants.Chart_Orange500,
    constants.Chart_GreenB500,
    constants.Chart_Green300,
    constants.Chart_Blue300,
    constants.Chart_Violet300,
    constants.Chart_Yellow300,
    constants.Chart_Brown300,
    constants.Chart_Purple300,
    constants.Chart_Orange300,
    constants.Chart_GreenB300,
    constants.Chart_Green100,
    constants.Chart_Blue100,
    constants.Chart_Violet100,
    constants.Chart_Yellow100,
    constants.Chart_Brown100,
    constants.Chart_Purple100,
    constants.Chart_Orange100,
    constants.Chart_GreenB100,
    constants.Chart_Yellow700,
    constants.Chart_Orange700,
    constants.Chart_GreenB700
];

export function getChartItemColor(index: number): string {
    if (index < chartColorsSortedList.length) {
        return chartColorsSortedList[index];
    }

    return chartColorsSortedList[index % chartColorsSortedList.length];
}
