import * as ColorOperations from "bobwai--color-operations";

// ---------- helpers -----------
export function getTransparentColor(hexcolor: string, alpha: number): string {
    const colorRgb = ColorOperations.hexToRgb(hexcolor);
    return "rgba(" + colorRgb[0] + ", " + colorRgb[1] + ", " + colorRgb[2] + ", " + alpha + ")";
}
