import * as Container from "bobwai--viewer-container";
import * as Example from "bobwai--example";
import * as menuWithoutIcons from "../src/menuWithoutIcons";
import * as menuWithIcons from "../src/menuWithIcons";
import * as wizard from "../src/wizard";
import * as dataHelper from "../src/dataHelper";

Example.init(
    {
        preferredHeight: Example.PreferredHeight.XLarge,
    },
    () => {
        return [
            Container.create({
                header: "Vertical submenu without icons (Medium size of items)",
                content: dataHelper.getSidebar(menuWithoutIcons.getItems(), true),
            }),
            Container.create({
                header: "Vertical submenu with icons (Default+Small size of items)",
                content: dataHelper.getSidebar(menuWithIcons.getItems(), false),
            }),
            Container.create({
                header: "Processing wizard (Default size of items)",
                content: dataHelper.getSidebar(wizard.getItems(), false),
            }),
        ];
    }
);
