import * as b from "bobril";
import * as SidebarItem from "bobwai--sidebar-item";
import * as dataHelper from "../src/dataHelper";

export function getItems(): b.IBobrilNode[] {
    return data.map((item: SidebarItem.IData) => {
        return SidebarItem.create(item);
    });
}
const data: SidebarItem.IData[] = [
    { id: "1", onClick: onClick, size: SidebarItem.size.Medium, title: "Inbox", counter: "7" },
    { id: "2", onClick: onClick, size: SidebarItem.size.Medium, title: "Sent", counter: "8", isActive: true },
    { id: "3", onClick: onClick, size: SidebarItem.size.Medium, title: "Not assigned", counter: "108" },
    { id: "4", onClick: onClick, size: SidebarItem.size.Medium, title: "All writers", counter: "46" },
    { id: "5", onClick: onClick, size: SidebarItem.size.Medium, title: "Long text Long text Long text Long text", counter: "0" },
];

function onClick(item: SidebarItem.IData): boolean {
    dataHelper.setActiveItem(data, item.id);
    b.invalidate();
    return true;
}
