import * as b from "bobril";

export interface ITestStore {
    isContextMenuVisible: boolean;
    itemsClickCount: number;
    itemsWrapperClickCount: number;
    lastClickedItemId?: string;
    mouseEvent?: b.IBobrilMouseEvent;
}

const testStore: ITestStore = {
    isContextMenuVisible: false,
    itemsClickCount: 0,
    itemsWrapperClickCount: 0,
    lastClickedItemId: ""
};

export function getTestStore(): ITestStore {
    return testStore;
}

export function clearTestStore(): void {
    testStore.isContextMenuVisible = false;
    testStore.itemsClickCount = 0;
    testStore.lastClickedItemId = "";
}
