export namespace Selectors {
    export const id = ".bobwai--context-menu";
    export const contentVirtualWrapper = ".bobwai--context-menu-content-wrapper";
    export const content = ".bobwai--context-menu-content";
    export const contentKey = "#bobwai--context-menu-content";
    export const items = content + "/~#context-menu-item-*";
    export function getItemById(id: string): string {
        return content + `/~#context-menu-item-${id}`;
    }
    export function getContentById(id: string): string {
        return contentKey + `-${id}`;
    }
    export function getContentVirtualWrapperByContentId(id: string): string {
        return getContentById(id) + "/^";
    }
}
