import * as b from "bobril";
import * as example from "../src/lib";

let data: example.IExampleOptions = {};

// registering listener of custom event, which reports the options of the example
example.addExampleInfoEventListener(document, (options: example.IExampleOptions) => {
    console.log("options:", options);
    data = options;
    b.invalidate();
});

example.init({ preferredHeight: 600, debug: true }, () => {
    return "Value passed by example info event " + JSON.stringify(data);
});
