import * as example from "../src/lib";

let data: string = "";

// registering listener of custom event, which can pass options to initialize example
example.addExampleG11nInitEventListener(document, (g11nInit: example.IExampleInit) => {
    g11nInit.g11nConfig = {
        pathToTranslation(locale: string): string {
            data = locale;
            return "/lib/" + locale + ".js";
        },
    };
});

example.init({}, () => {
    return 'Language requested for g11n init "' + data + '"';
});
