export function getComponentNameCamelCase(fullComponentName: string): string {
    const name = fullComponentName.split("--")[1];
    const nameParts = name.split("-");
    const convertedWords = [];
    for (let i = 0; i < nameParts.length; i++) {
        let word = nameParts[i];
        word = word[0].toUpperCase() + word.substr(1, word.length);
        convertedWords.push(word);
    }
    return convertedWords.join("");
}
