import * as b from "bobril";
import { Filter } from "../../src/lib";
import { getTestStore } from "./store";

export function createLayout(): void {
    b.init(() => {
        return [
            Filter({
                value: getTestStore().value,
                onChange: (v) => {
                    getTestStore().value = v;
                    b.invalidate();
                },
                onTextClear: () => {
                    getTestStore().value = "";
                    b.invalidate();
                },
                clearTextOnEscape: true,
            }),
            Filter({
                value: getTestStore().disabledValue,
                onChange: (v) => {
                    getTestStore().disabledValue = v;
                    b.invalidate();
                },
                onTextClear: () => {
                    getTestStore().disabledValue = "";
                    b.invalidate();
                },
                isDisabled: true,
            }),
        ];
    });
}
