import * as b from "bobril";
import * as ColorModifier from "bobwai-debug--color-modifier";

import * as font from "../src/lib";

font.init();

b.init(() => {
    return [
        ColorModifier.create({}),
        text("Light", font.FontWeightLight),
        text("Regular", font.FontWeightRegular),
        text("Semibold", font.FontWeightSemibold),
        text("Bold", font.FontWeightBold),

        text("Light Italic", font.FontWeightLight, true),
        text("Regular Italic", font.FontWeightRegular, true),
        text("Semibold Italic", font.FontWeightSemibold, true),
        text("Bold Italic", font.FontWeightBold, true),

        text("Droid", font.FontWeightRegular, false, font.MonospacedFontFamily),

        text("Muli Light", font.FontWeightLight, false, font.MuliFontFamily),
        text("Muli Regular", font.FontWeightRegular, false, font.MuliFontFamily),

        "Default Text:",
        "Lorem ipsum Occaecat voluptate sunt aute esse qui quis labore tempor esse enim aliquip sit laborum."
    ];
});

function text(text: string, fontWeight: string | number, isItalic: boolean = false, fontFamily?: string) {
    return {
        tag: "div",
        style: {
            fontFamily: fontFamily,
            fontSize: fontFamily ? "22px" : "20px",
            fontWeight: fontWeight,
            fontStyle: isItalic && "italic"
        },
        children: "Text with font-face: " + text
    };
}
