import * as b from "bobril";

import * as constants from "./publicConstants";
import * as bobwai__font from "./assets";

export function getCss(fontColor: string): string {
    return `
body,
button,
input,
select,
textarea {
    font-family: ${constants.FontFamily};
    font-size: ${constants.FontSizeDefault};
}

body {
    color: ${fontColor};
}


@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: ${constants.FontWeightLight};
    src: local('Open Sans Light'), local('OpenSans-Light'), url('${b.asset(bobwai__font.open_sans_light_woff)}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: ${constants.FontWeightRegular};
    src: local('Open Sans'), local('OpenSans'), url('${b.asset(bobwai__font.open_sans_normal_woff)}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: ${constants.FontWeightSemibold};
    src: local('Open Sans Semibold'), local('OpenSans-Semibold'), url('${b.asset(bobwai__font.open_sans_semibold_woff)}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: ${constants.FontWeightBold};
    src: local('Open Sans Bold'), local('OpenSans-Bold'), url('${b.asset(bobwai__font.open_sans_bold_woff)}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: italic;
    font-weight: ${constants.FontWeightLight};
    src: local('Open Sans Light Italic'), local('OpenSansLight-Italic'), url('${b.asset(
        bobwai__font.open_sans_light_italic_woff
    )}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: italic;
    font-weight: ${constants.FontWeightRegular};
    src: local('Open Sans Italic'), local('OpenSans-Italic'), url('${b.asset(bobwai__font.open_sans_normal_italic_woff)}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: italic;
    font-weight: ${constants.FontWeightSemibold};
    src: local('Open Sans Semibold Italic'), local('OpenSans-SemiboldItalic'), url('${b.asset(
        bobwai__font.open_sans_semibold_italic_woff
    )}') format('woff');
}
@font-face {
    font-family: 'Open Sans';
    font-style: italic;
    font-weight: ${constants.FontWeightBold};
    src: local('Open Sans Bold Italic'), local('OpenSans-BoldItalic'), url('${b.asset(
        bobwai__font.open_sans_bold_italic_woff
    )}') format('woff');
}
@font-face {
    font-family: 'Droid Sans Mono';
    font-style: normal;
    font-weight: ${constants.FontWeightRegular};
    src: local('Droid Sans Mono'), local('DroidSansMono'), url('${b.asset(bobwai__font.droid_sans_mono_woff)}') format('woff');
}
@font-face {
    font-family: 'Muli';
    font-style: normal;
    font-weight: ${constants.FontWeightLight};
    src: local('Muli Light'), local('Muli-Light'), url('${b.asset(bobwai__font.muli_light_woff)}') format('woff');
}
@font-face {
    font-family: 'Muli';
    font-style: normal;
    font-weight: ${constants.FontWeightRegular};
    src: local('Muli'), local('Muli'), url('${b.asset(bobwai__font.muli_normal_woff)}') format('woff');
}
`;
}
