import * as b from "bobril";
import * as Example from "bobwai--example";
import * as Container from "bobwai--viewer-container";
import * as Combobox from "bobwai--combobox";
import * as Checkbox from "bobwai--checkbox";
import * as Footer from "../src/lib";

let defaultData: Footer.IData = {
    footerText: "Â© GMC Software AG"
};

Example.init(
    {
        preferredHeight: Example.PreferredHeight.Medium
    },
    () => {
        return [
            Example.createControlPanel({
                items: [
                    Example.createField({
                        label: "hasTopBorder",
                        fieldType: Example.FieldType.Checkbox,
                        value: defaultData.hasTopBorder,
                        fieldFactory: Checkbox.create,
                        onChange: v => {
                            defaultData.hasTopBorder = v;
                            b.invalidate();
                        }
                    }),
                    Example.createField({
                        label: "variant",
                        customValueContent: Example.createComboEnum({
                            enum: Footer.Variant,
                            value: defaultData.variant,
                            comboboxFactory: Combobox.create,
                            onChange: v => {
                                defaultData.variant = v;
                                b.invalidate();
                            }
                        })
                    })
                ]
            }),
            Container.create({
                header: "bobwai--footer",
                content: Footer.create(defaultData)
            })
        ];
    }
);
