import * as b from "bobril";

export enum ContentTopIndent {
    Default = 25,
    None = 0,
    Small = 7,
    Medium = 15,
}

export function getContentTopIndentStyle(topIndent?: ContentTopIndent): b.IBobrilStyleDef {
    switch (topIndent) {
        case ContentTopIndent.None:
            return contentTopIndentNoneStyle;
        case ContentTopIndent.Small:
            return contentTopIndentSmallStyle;
        case ContentTopIndent.Medium:
            return contentTopIndentMediumStyle;
        default:
            return contentTopIndentDefaultStyle;
    }
}

const baseStyle: b.IBobrilStyle = {
    boxSizing: "border-box",
};

const contentTopIndentDefaultStyle = b.styleDef([
    baseStyle,
    {
        paddingTop: ContentTopIndent.Default,
    },
]);

const contentTopIndentNoneStyle = b.styleDef([
    baseStyle,
    {
        paddingTop: ContentTopIndent.None,
    },
]);

const contentTopIndentSmallStyle = b.styleDef([
    baseStyle,
    {
        paddingTop: ContentTopIndent.Small,
    },
]);

const contentTopIndentMediumStyle = b.styleDef([
    baseStyle,
    {
        paddingTop: ContentTopIndent.Medium,
    },
]);
