import * as b from "bobril";
import * as IconCheckbox from "../../src/lib";
import { getTestStore } from "./store";

export function createLayout(): void {
    b.init(() => {
        return [
            IconCheckbox.create({
                value: getTestStore().value,
                checkedImage: mockIcon,
                uncheckedImage: mockHoveredIcon,
                onChange: v => {
                    getTestStore().value = v;
                    b.invalidate();
                }
            })
        ];
    });
}

const mockIconStyle = b.styleDef({
    height: 16,
    width: 16,
    boxSizing: "border-box",
    display: "inline-block",
    verticalAlign: "middle",
    border: "2px solid"
});

const mockIcon = b.styledDiv("", mockIconStyle);
const mockHoveredIcon = b.styledDiv("", mockIconStyle, { background: "green" });
