import * as b from "bobril";
import * as L_App from "../src/lib";
import * as Container from "bobwai--viewer-container";
import * as Example from "bobwai--example";

const sampleContent = b.styledDiv("Some content", b.styleDef({ background: "lime", margin: 0, padding: 0, height: "100%" }));

Example.init({}, () => {
    return [
        Container.create({
            header: "L_App",
            content: L_App.create({
                children: sampleContent,
            }),
        }),
        Container.create({
            header: "should not set min-width for mobile",
            content: L_App.create({ children: sampleContent, isMobile: true }),
        }),
        Container.create({
            content: {
                tag: "a",
                children: "For complex example visit the bobwai--composition--application-views",
                attrs: {
                    href: "http://gallery/app/#/component/bobwai--composition--application-views",
                },
            },
        }),
    ];
});
