import * as Color from "bobwai--color";
import * as ColorOperations from "bobwai--color-operations";

export const BackgroundColor = Color.Grey100;
export const Height = 40;

let propBorderColor: Color.IColor = Color.Grey100;
export function BorderColor(): string {
    return ColorOperations.makeFixedValue(propBorderColor);
}
export function setBorderColor(val: Color.IColor) {
    propBorderColor = val;
}

let propContentPadding: number = 25;
export function ContentPadding(): number {
    return propContentPadding;
}
export function setContentPadding(val: number) {
    propContentPadding = val;
}

let propContentPaddingSmall: number = 12;
export function ContentPaddingSmall(): number {
    return propContentPaddingSmall;
}
export function setContentPaddingSmall(val: number) {
    propContentPaddingSmall = val;
}
