import * as b from "bobril";
import * as Example from "bobwai--example";
import * as L_App from "bobwai--l-app";
import * as LViewAppHeader from "../src/lib";

const sampleContent = b.styledDiv("Header - should never have browser scrollbar at bottom", b.styleDef({ background: "lime" }));

Example.init({}, () => {
    return [
        L_App.create({
            isMobile: true,
            children: LViewAppHeader.create({
                isMobile: true,
                children: sampleContent,
            }),
        }),
    ];
});
