import * as b from "bobril";
import * as Toolbar from "bobwai--toolbar";
import * as LViewAppHeader from "bobwai--l-view-app-header";
import * as LViewBreadCrumb from "bobwai--l-view-app-breadcrumb";
import * as L_App from "bobwai--l-app";

export const baseStyle = b.styleDef(
    {
        position: "fixed",
        top: 0,
        height: Toolbar.Height,
        width: "100%",
        minWidth: L_App.MinWidth
    },
    {},
    "l-view-app-toolbar"
);

export const smallToolbarStyle = b.styleDefEx(baseStyle, {
    height: Toolbar.SmallHeight
});

export const withAppHeaderStyle = b.styleDefEx(baseStyle, {
    top: LViewAppHeader.Height
});

export const withAppBreadcrumbStyle = b.styleDefEx(baseStyle, {
    top: LViewBreadCrumb.Height
});

export const withAppBreadcrumbAndAppHeaderStyle = b.styleDefEx(baseStyle, {
    top: LViewBreadCrumb.Height + LViewAppHeader.Height
});
