import * as LViewAppHeader from "bobwai--l-view-app-header";
import * as LViewAppBreadCrumb from "bobwai--l-view-app-breadcrumb";
import * as LViewAppToolbar from "bobwai--l-view-app-toolbar";

export function getPaddingTopStyle(
    isCombinedWithAppHeader?: boolean,
    isCombinedWithAppBreadcrumb?: boolean,
    isCombinedWithAppToolbar?: boolean,
    toolbarSize?: LViewAppToolbar.Size
) {
    let paddingTop = 0;

    if (isCombinedWithAppHeader) {
        paddingTop += LViewAppHeader.Height;
    }

    if (isCombinedWithAppBreadcrumb) {
        paddingTop += LViewAppBreadCrumb.Height;
    }

    if (isCombinedWithAppToolbar) {
        switch (toolbarSize) {
            case LViewAppToolbar.Size.Small:
                paddingTop += LViewAppToolbar.SmallHeight;
                break;

            default:
                paddingTop += LViewAppToolbar.Height;
                break;
        }
    }

    return {
        paddingTop: paddingTop
    };
}
