// styles
export const fullHeightStyle = b.styleDef(
    {
        height: "100%",
    },
    {},
    "scroll-full-height"
);

export const wrapperStyle = b.styleDef(
    {
        position: "relative",
    },
    {},
    "scroll-wrapper"
);

export const overflowHiddenStyle = b.styleDef({
    overflow: "hidden",
});

export const contentStyle = b.styleDef(
    {
        overflow: "auto",
    },
    {},
    "scroll-content"
);

export const contentRelativeStyle = b.styleDef(
    {
        position: "relative",
    },
    {},
    "scroll-content-relative"
);

export const contentSidePaddingStyle = b.styleDef(
    {
        paddingLeft: sidePadding,
        paddingRight: sidePadding,
    },
    {},
    "scroll-content-with-padding"
);

export const contentSidePaddingForAsterisksStyle = b.styleDef(
    {
        marginLeft: -1 * sidePaddingForAsterisks,
        paddingLeft: sidePaddingForAsterisks,
    },
    {},
    "scroll-content-with-padding-for-asterisks"
);

export const shadowStyle = b.styleDef(
    {
        position: "absolute",
        opacity: 0,
        height: shadowHeight,
        marginLeft: shadowBoundary,
        marginRight: shadowBoundary,
        backgroundRepeat: "repeat-x",
        pointerEvents: "none",
    },
    {
        before: [
            {
                position: "absolute",
                display: "block",
                content: "",
                top: 0,
                left: -shadowBoundary,
                width: shadowBoundary,
                height: shadowHeight,
                pointerEvents: "none",
            },
        ],
        after: [
            {
                position: "absolute",
                display: "block",
                content: "",
                top: 0,
                right: -shadowBoundary,
                width: shadowBoundary,
                height: shadowHeight,
                pointerEvents: "none",
            },
        ],
    },
    "scroll-shadow"
);

export const topShadowStyle = b.styleDef(
    {
        top: 0,
        backgroundImage: "url(" + topShadowImage + ")",
    },
    {
        before: {
            backgroundImage: "url(" + topCornerLeftImage + ")",
        },
        after: {
            backgroundImage: "url(" + topCornerRightImage + ")",
        },
    },
    "scroll-shadow-top"
);

export const bottomShadowStyle = b.styleDef(
    {
        bottom: 0,
        backgroundImage: "url(" + bottomShadowImage + ")",
    },
    {
        before: {
            backgroundImage: "url(" + bottomCornerLeftImage + ")",
        },
        after: {
            backgroundImage: "url(" + bottomCornerRightImage + ")",
        },
    },
    "scroll-shadow-bottom"
);

// just for identification in DOM
export const scrollShadowWithOffsetWrapperStyle = b.styleDef({});

export const scrollShadowWithOffsetWrapperFullSizeStyle = b.styleDef({
    height: "100%",
});

export const scrollShadowOffsetIECorrection = b.styleDef({ paddingBottom: 1, marginBottom: -1 });

export const shadowLeftSidePaddingStyle = b.styleDefEx(shadowStyle, {
    left: sidePadding,
});

export const scrollShadowBottomBorderStyle = b.styleDef(
    {
        position: "absolute",
        bottom: -bottomBorderHeight,
        left: 0,
        right: 0,
        height: 1,
        background: Color.Grey300,
    },
    {},
    "scroll-shadow-bottom-border"
);

export const scrollShadowWithBottomBorderStyle = b.styleDef(
    {
        paddingBottom: bottomBorderHeight,
        boxSizing: "border-box",
    },
    {},
    "scroll-shadow-with-bottom-border"
);
