import * as b from "bobril";
import * as Container from "bobwai--viewer-container";
import * as Example from "bobwai--example";
import * as Icon from "bobwai--icon";
import * as Color from "bobwai--color";
import * as SidebarFooter from "../src/lib";
import * as bobwai__sidebar_footer from "../src/assets";

let logo = Icon.create({ sprite: b.sprite(bobwai__sidebar_footer.logo_neopost_png) });

Example.init(
    {
        preferredHeight: Example.PreferredHeight.Large
    },
    () => {
        return [
            Container.create({
                header: "Bobwai--sidebar-footer with all variant",
                content: b.styledDiv(SidebarFooter.create({ leftContent: logo, rightContent: "Copyright © 2016" }), {
                    backgroundColor: Color.White
                })
            }),
            Container.create({
                header: "Bobwai--sidebar-footer with left content",
                content: b.styledDiv(SidebarFooter.create({ leftContent: logo }), { backgroundColor: Color.White })
            }),
            Container.create({
                header: "Bobwai--sidebar-footer with right content",
                content: b.styledDiv(SidebarFooter.create({ rightContent: "Copyright © 2016" }), { backgroundColor: Color.White })
            })
        ];
    }
);
