import * as c from "./constants";
import * as Color from "bobwai--color";
import * as ColorOperations from "bobwai--color-operations";

export const ItemCollapsibleContentPadding = c.itemCollapsibleContentPadding;
export const Border = c.border;
export const SeparatorColor = c.separatorColor;

let propCollapsibleIconColor: Color.IColor = Color.Grey500;
export function CollapsibleIconColor(): string {
    return ColorOperations.makeFixedValue(propCollapsibleIconColor);
}
export function setCollapsibleIconColor(val: Color.IColor) {
    propCollapsibleIconColor = val;
}
