import * as b from "bobril";
import * as c from "./constants";
import * as Mixins from "bobwai--style-mixins";

export const baseStyle = b.styleDef(
    [
        Mixins.getUnstyledLink(),
        Mixins.getClickableElement(),
        {
            display: "block",
            paddingTop: 0,
            paddingBottom: 0,
            paddingLeft: c.paddingLeft,
            fontWeight: c.fontWeight,
            lineHeight: c.lineHeight,
            fontSize: c.fontSize,
            borderBottom: c.border,
            color: c.color,
            position: "relative",
            backgroundColor: c.backgroundColor,
        },
    ],
    {},
    "sidebarItemBaseStyle"
);

export const cjmStyle = b.styleDefEx(
    baseStyle,
    {
        paddingLeft: c.sidePaddingCJM,
    },
    {},
    "sidebarItemCjmStyle"
);

export const isDisabledChildrenStyle = b.styleDef({
    opacity: 0.4,
});

export const noDropCursor = b.styleDef({
    cursor: "no-drop",
});
