import * as b from "bobril";
import * as Mixins from "bobwai--style-mixins";
import * as Color from "bobwai--color";

export const create = b.createComponent<IData>({
    id: "bobwai--sidebar",
    render(ctx: IContext, me: b.IBobrilNode): void {
        me.children = ctx.data.children;
        b.style(me, baseStyle);
    },
});

export interface IData {
    children?: b.IBobrilChildren;
}

interface IContext extends b.IBobrilCtx {
    data: IData;
}

namespace localVariables {
    export const border = "none";
    export const background = Color.White;
}

const baseStyle = b.styleDef([
    {
        background: localVariables.background,
        borderRight: localVariables.border,
        height: "100%",
    },
    Mixins.getUnstyledList(),
]);

// added by script - BEGIN
export const Sidebar = create;
// added by script - END
