import * as b from "bobril";
import * as Icon from "bobwai--icon";
import * as Color from "bobwai--color";
import { getTestStore } from "./store";
import { StateIcon } from "../../src/lib";

const icon = Icon.create({ sprite: b.sprite(Icon.close_xsmall_png, Color.Basic) });
const hoverIcon = Icon.create({ sprite: b.sprite(Icon.close_xsmall_png, Color.Error) });

export function createLayout(): void {
    b.init(() => {
        return [
            StateIcon({
                content: icon,
                hoverContent: hoverIcon,
                onClick: () => {
                    getTestStore().clickCount += 1;
                    b.invalidate();
                }
            })
        ];
    });
}
