import * as b from "bobril";
import * as mixins from "../src/lib";
import * as Example from "bobwai--example";

Example.init({}, () => {
    return [
        b.styledDiv("Shadows mixins:", mockTitleStyle),
        getMockTileElement("Drop shadow 100", mixins.shadowStylesMixins.getDropShadow100()),
        getMockTileElement("Drop shadow 200", mixins.shadowStylesMixins.getDropShadow200()),
        getMockTileElement("Drop shadow 300", mixins.shadowStylesMixins.getDropShadow300()),
        getMockTileElement("Drop shadow 400", mixins.shadowStylesMixins.getDropShadow400()),

        b.styledDiv("Heading mixins (for complete preview with correct font see bobwai--header-text):", mockTitleStyle),
        getMockTextElement("Heading 100", mixins.headingStyleMixins.getHeading100TextStyle()),
        getMockTextElement("Heading 150", mixins.headingStyleMixins.getHeading150TextStyle()),
        getMockTextElement("Heading 200", mixins.headingStyleMixins.getHeading200TextStyle()),
        getMockTextElement("Heading 300", mixins.headingStyleMixins.getHeading300TextStyle()),
        getMockTextElement("Heading 400", mixins.headingStyleMixins.getHeading400TextStyle()),
        getMockTextElement("Heading 500", mixins.headingStyleMixins.getHeading500TextStyle()),

        b.styledDiv("Spacing:", mockTitleStyle),
        getMockSpacingElement("Spacing100", mixins.Spacing.Spacing100, "#FFA07F"),
        getMockSpacingElement("Spacing200", mixins.Spacing.Spacing200, "#BEAAE0"),
        getMockSpacingElement("Spacing300", mixins.Spacing.Spacing300, "#88D9CC"),
        getMockSpacingElement("Spacing400", mixins.Spacing.Spacing400, "#F8C48B"),
        getMockSpacingElement("Spacing500", mixins.Spacing.Spacing500, "#80B5D2"),
        getMockSpacingElement("Spacing600", mixins.Spacing.Spacing600, "#E57FCC"),
        getMockSpacingElement("Spacing700", mixins.Spacing.Spacing700, "#B3D792"),
        getMockSpacingElement("Spacing800", mixins.Spacing.Spacing800, "#C6B6B0"),
    ];
});

function getMockTileElement(name: string, style: b.IBobrilStyle): b.IBobrilNode {
    return b.styledDiv(name, [mockTileStyle, style]);
}
function getMockTextElement(name: string, style: b.IBobrilStyle): b.IBobrilNode {
    return b.styledDiv(name, style);
}
function getMockSpacingElement(name: string, value: number, color: string): b.IBobrilNode {
    return b.styledDiv(
        [
            b.styledDiv(`${name} (${value} px)`, { color, display: "inline-block", minWidth: 200, verticalAlign: "top" }),
            b.styledDiv("", { background: color, width: value, height: value, display: "inline-block", verticalAlign: "top" }),
        ],
        { marginBottom: 5 }
    );
}

const mockTileStyle = b.styleDef({
    margin: 30,
    width: 150,
    height: 150,
    background: "rgb(251,251,251)",
    display: "inline-block",
});

const mockTitleStyle = b.styleDef({
    marginBottom: 10,
    marginTop: 20,
    fontWeight: "bold",
});
