import * as b from "bobril";

export namespace ValidationError {
    // this constants is used as width of error icon (exclamation mark) behind input with some validation error
    export const errorIconWidth: number = 12;

    // this style is for input validation message to be readable for screen reader but invisible in DOM
    export const inputWCAGValidationMessageHiddenStyle = b.styleDef({
        height: 0,
        width: 0,
        fontSize: 0,
        position: "absolute",
    });
}
