import { ISelection } from "../../src/lib";

export interface ITestStore {
    value: string;
    selection: ISelection;
    isFocused: boolean;
}

let testStore: ITestStore = getDefaultStore();

export function getTestStore(): ITestStore {
    return testStore;
}

export function clearTestStore(): void {
    testStore = getDefaultStore();
}

function getDefaultStore(): ITestStore {
    return {
        value: "some text",
        selection: { start: 2, end: 6 },
        isFocused: false,
    };
}
