import { BBSeeker, BBBot } from "bbseeker-extended";
import * as Textarea from "../src/lib";
import * as b from "bobril";

describe("bobwai--textarea test", () => {
    beforeEach(() => {
        b.init(() => {
            return [
                Textarea.create({
                    placeholder: "placeholder",
                    value: "Lorem Ipsum",
                    isDisabled: true,
                }),
            ];
        });
    });
    it("should placeholder and value", async () => {
        await BBBot.checkIfFullyRendered(5);
        const textarea = await BBSeeker.findElement(".bobwai--textarea/textarea");
        expect(textarea).toBeDefined();
        expect((<HTMLTextAreaElement>textarea).value).toBe("Lorem Ipsum");
        expect((<HTMLTextAreaElement>textarea).placeholder).toBe("placeholder");
        expect((<HTMLTextAreaElement>textarea).disabled).toBe(true);
    });
});
