export interface ITestStore {
    value: string;
    disabledValue: string;
    withIconEnabledValue: string;
    withIconDisabledValue: string;
    iconClicks: number;
}

const testStore: ITestStore = {
    value: "",
    disabledValue: "",
    withIconEnabledValue: "",
    withIconDisabledValue: "",
    iconClicks: 0,
};

export function getTestStore(): ITestStore {
    return testStore;
}

export function clearTestStore(): void {
    testStore.value = "";
    testStore.disabledValue = "";
    testStore.withIconEnabledValue = "";
    testStore.withIconDisabledValue = "";
    testStore.iconClicks = 0;
}
