import * as b from "bobril";
import * as Container from "bobwai--viewer-container";
import * as Example from "bobwai--example";
import * as Color from "bobwai--color";
import * as Toolbar from "../src/lib";

Example.init({}, () => {
    return [
        Container.create({
            header: "bobwai--toolbar - items on left side",
            content: Toolbar.create({
                items: getItems()
            })
        }),
        Container.create({
            header: "bobwai--toolbar - items on right side",
            content: Toolbar.create({
                itemsRight: getItems()
            })
        }),
        Container.create({
            header: "bobwai--toolbar - items on both sides",
            content: Toolbar.create({
                items: getItems(),
                itemsRight: getItems()
            })
        }),
        Container.create({
            header: "bobwai--toolbar left items and center content",
            content: Toolbar.create({
                items: getItems(),
                centerContent: getMockCenterContent()
            })
        }),
        Container.create({
            header: "bobwai--toolbar right items and center content",
            content: Toolbar.create({
                itemsRight: getItems(),
                centerContent: getMockCenterContent()
            })
        }),
        Container.create({
            header: "bobwai--toolbar left items, center content and right items",
            content: Toolbar.create({
                items: getItems(),
                itemsRight: getItems(),
                centerContent: getMockCenterContent()
            })
        }),
        Container.create({
            header: "bobwai--toolbar  center content only",
            content: Toolbar.create({
                centerContent: getMockCenterContent()
            })
        }),
        Container.create({
            header: "bobwai--toolbar skinny variant",
            content: Toolbar.create({
                items: getItems(),
                itemsRight: getItems(),
                size: Toolbar.Size.Small
            })
        })
    ];
});

function getItems(): b.IBobrilNode[] {
    return [
        b.styledDiv("sample", { width: 50, height: "100%", background: Color.Application }),
        b.styledDiv("sample", { width: 100, height: "100%", background: Color.Chart_Green100 }),
        b.styledDiv("sample", { width: 50, height: "100%", background: Color.Grey600 }),
        b.styledDiv("sample", { width: 80, height: "100%", background: Color.Application_30 })
    ];
}

function getMockCenterContent(): b.IBobrilNode {
    return b.styledDiv("Center content", { background: Color.Error_10, height: "100%" });
}
