import { BBSeeker, BBBot } from "bbseeker-extended";
import * as TooltipMessage from "../src/lib";
import * as b from "bobril";

describe("bobwai--tooltip-message test", () => {
    beforeEach(() => {
        b.init(() => {
            return [
                TooltipMessage.create({
                    header: ["header1", "header2"],
                    message: ["message1", "message2"],
                    placement: TooltipMessage.Placement.right
                })
            ];
        });
    });
    it("should find the right element according the key", async () => {
        await BBBot.checkIfFullyRendered(5);
        const content = await BBSeeker.findElements(".bobwai--tooltip-message/div");
        expect(content.length).toBe(5);
        expect(content[0].textContent).toBe("header1");
        expect(content[3].textContent).toBe("message2");
    });
});
