import * as Color from "bobwai--color";
import * as FormElements from "bobwai--form-elements";

export interface IData {
    message?: string | string[];
    header?: string | string[];
    placement?: Placement;
    validationState?: FormElements.ValidationState;
    customTheme?: ITooltipTheme;
    hasBorder?: boolean;
    zoomRatio?: number;
    ellipsisWidth?: EllipsisWidth;
    variant?: Variant;
}

export enum Variant {
    Default = "Default",
    Satellite = "Satellite"
}

export enum Placement {
    left,
    right,
    topCenter,
    topLeft,
    topRight,
    bottomCenter,
    bottomLeft,
    bottomRight
}

export enum EllipsisWidth {
    None,
    Normal
}

export interface ITooltipTheme {
    background: Color.IColor;
}
