import * as b from "bobril";

interface IData {
    text: string;
    onClick: () => void;
}

interface IContext extends b.IBobrilCtx {
    data: IData;
}

export let create = b.createComponent<IData>({
    id: "bobwai--example-color-modifier-link",
    render(ctx: IContext, me: b.IBobrilNode) {
        me.children = ctx.data.text;
        b.style(me, simpleExampleLinkStyle);
    },
    onClick(ctx: b.IBobrilCtx, _e: b.IBobrilMouseEvent): boolean {
        ctx.data.onClick();
        return false;
    }
});

const simpleExampleLinkStyle = b.styleDef(
    {
        color: "#3598db",
        display: "inline-block",
        cursor: "pointer"
    },
    { hover: { textDecoration: "underline" } }
);
