import * as Example from "bobwai--example";
import * as Container from "bobwai--viewer-container";
import * as Debugger from "../src/lib";

Example.init({}, () => {
    return [
        Container.create({
            header: "debugger - absolute (should be on the right side)",
            content: [
                Debugger.create({
                    content: "Debugger - absolute position"
                }),
                "Some content below"
            ]
        }),
        Container.create({
            header: "debugger - fixed position(will be top right of whole browser)",
            content: [
                Debugger.create({
                    content: "Debugger - fixed position (min-width: 250)",
                    isPositionFixed: true,
                    isTopAligned: true,
                    minWidth: 250
                })
            ]
        })
    ];
});
