import * as b from "bobril";
import * as Container from "bobwai--viewer-container";
import * as Example from "bobwai--example";
import * as browserIdentifier from "../src/lib";
import * as LField from "bobwai--l-field";
import * as Icon from "bobwai--icon";
import * as Color from "bobwai--color";
import * as HorizontalLine from "bobwai--horizontal-line";

const isBrowserIcon = b.sprite(Icon.check_in_circle_medium_png, Color.Success);
const isNotBrowserIcon = b.sprite(Icon.decline_medium_png, Color.Error);

Example.init({}, () => {
    return [
        Container.create({
            header: "browser-identifier",
            isTest: true,
            content: [
                getBrowserLField("Chrome", browserIdentifier.isChrome()),
                HorizontalLine.create(),
                getBrowserLField("Firefox", browserIdentifier.isFirefox()),
                HorizontalLine.create(),
                getBrowserLField("Edge", browserIdentifier.isEdge()),
                HorizontalLine.create(),
                getBrowserLField("Internet explorer", browserIdentifier.isIE()),
                HorizontalLine.create(),
                getBrowserLField("Safari", browserIdentifier.isSafari())
            ]
        })
    ];
});

function getBrowserLField(label: string, isBrowser: boolean) {
    return LField.create({
        labelContent: label,
        valueContent: Icon.create({ sprite: isBrowser ? isBrowserIcon : isNotBrowserIcon })
    });
}
