import * as b from "bobril";
import { ColumnCount } from "bobwai--form-elements";

export * from "./publicConstants";
export * from "./setSizeAccordingFormContext";

export const FormContext = b.createContext<IFormContext>({});

export interface IFormContext {
    formSettings?: IFormSettings;
    useVerticalFields?: boolean;
}

export interface IFormSettings {
    variant?: FormVariant;
    widthSettings?: IFormWidthSettings;
}

export interface IFormWidthSettings {
    // default is 2 columns
    labelWidth?: ColumnCount;
    // default is auto
    valueWidth?: ColumnCount;
    // default is 1
    actionsWidth?: ColumnCount;
}

export enum FormVariant {
    Default,
    Reduced,
}
