import * as b from "bobril";
export function handleMouseWheelScroll(element: HTMLElement, event: b.IBobrilMouseWheelEvent): boolean {
    let isScrollAtTop = element.scrollTop === 0;
    let isScrollAtBottom = Math.abs(element.scrollTop + element.clientHeight - element.scrollHeight) < 7;

    if (event.dy > 0 && isScrollAtBottom) {
        element.scrollTop = element.scrollHeight - element.clientHeight;
    }

    return element && ((isScrollAtTop && event.dy < 0) || (event.dy > 0 && isScrollAtBottom));
}