import * as flux from "bobflux";
import { IValidationsState } from "../state";
import { IValidationCursorFactoryParams, dynamicValidationsCursorFactory } from "../cursor";

export const clearError: flux.IAction<IValidationCursorFactoryParams> = flux.createAction<
    IValidationsState,
    IValidationCursorFactoryParams
>(dynamicValidationsCursorFactory, (state) =>
    flux.shallowCopy(state, (result: IValidationsState) => {
        result.errorInfo = undefined;
    })
);
