import * as flux from "bobflux";
import { IValidationsState } from "./state";

export interface IValidationCursorFactoryParams {
    cursor?: flux.ICursor<IValidationsState>;
}

export const dynamicValidationsCursorFactory: flux.ICursorFactory<IValidationsState, IValidationCursorFactoryParams> = {
    create: (params) => {
        return params && params.cursor ? params.cursor : { key: "" };
    },
};
